import React, { useState } from "react";
import styles from "./LandingRoute.module.css";
import LoginCard from "../UI/LoginCard";
import RegisterCard from "../UI/RegisterCard";

const LandingRoute = () => {
  const [showRegister, setShowRegister] = useState(false);
  const showRegisterCard = () =>{
    setShowRegister(!showRegister); 
  }

  const showLoginCard = () => {
    setShowRegister(false);
  }

  return (
    <div className={styles.welcomeContainer}>
      <h1 className={styles.heading}> Welcome to the Book Library!</h1>
      {showRegister ? <RegisterCard triggerLoginCard={showLoginCard}/> : <LoginCard triggerRegisterCard={showRegisterCard}/>}
    </div>
  );
};

export default LandingRoute;
