import styles from "./Header.module.css";
import React from "react";

const Header = ({ loginStatus, placeholder }) => {

    isLoggedIn = isLoggedIn;

  return (
    <div className={styles.headerContainer}>
      <a href="/">
        <h1>Book Library</h1>
      </a>

      {/* Search form */}
      {isLoggedIn && (
        <form action="/search" method="GET" className={styles.searchForm}>
          <div className={styles.searchInputContainer}>
            <input
              type="text"
              id="search"
              name="query"
              autoFocus
              autoComplete="off"
              placeholder={placeholder}
            />
            <button type="submit" className={styles.searchButton}>
              Find Books &#128269;
            </button>
          </div>
        </form>
      )}

      {!isLoggedIn && (
        <div className={styles.authButtons}>
          <form action="/login" method="GET" className={styles.authButtons}>
            <button type="submit" className={styles.authButton}>
              Login
            </button>
          </form>
          <form action="/register" method="GET" className={styles.authButtons}>
            <button type="submit" className={styles.authButton}>
              Register
            </button>
          </form>
        </div>
      )}

      {isLoggedIn && (
        <div className={styles.authButtons}>
          <form action="/savedBooks" method="GET" className={styles.savedBooks}>
            <button type="submit" className={styles.authButton}>
              Saved Books
            </button>
          </form>
          <form action="/logout" method="POST" className={styles.logoutForm}>
            <button type="submit" className={styles.authButton}>
              Logout
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Header;
