import React from "react";
import Header from "./components/Reusables/Header";
import ExploreBooks from "./components/Pages/ExploreBooks";
import LandingRoute from "./components/Pages/LandingRoute";
import Footer from "./components/Reusables/Footer";
import { AuthProvider, useAuth } from "./components/store/AuthContext";
import "./index.css";

function App() {
  const { isLoggedIn, logout } = useAuth();

  return (
    <AuthProvider>
      <div>
        <Header isLoggedIn={isLoggedIn} onLogout={logout} />{" "}
        {/* Pass isLoggedIn and logout */}
        {isLoggedIn ? <ExploreBooks /> : <LandingRoute />}
        <Footer />
      </div>
    </AuthProvider>
  );
}

export default App;
