import React, { useState } from "react";
import styles from "./GenreForm.module.css";

const GenreForm = ({ onGenreSelect }) => {
  const [selectedGenre, setSelectedGenre] = useState("Mystery");

  const handleGenreClick = (genre) => {
    setSelectedGenre(genre);
    onGenreSelect(genre);
  };

  return (
    <div>
      <form action="/" method="GET" className={styles.genreForm}>
        <div className={styles.genreButtons}>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Fantasy" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Fantasy")}
          >
            Fantasy
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Mystery" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Mystery")}
          >
            Mystery
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Fiction"
          >
            Fiction
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Crime"
          >
            Crime
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Science"
          >
            Science
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Narrative"
          >
            Narrative
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="History"
          >
            History
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Sci-Fi"
          >
            Sci-Fi
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Memoir"
          >
            Memoir
          </button>
          <button
            className={styles.genreButton}
            type="submit"
            name="selectedGenre"
            value="Self-Help"
          >
            Self-Help
          </button>
        </div>
      </form>
    </div>
  );
};

export default GenreForm;
