import React, { useState } from "react";
import styles from "./GenreForm.module.css";

const GenreForm = ({ onGenreSelect }) => {
  const [selectedGenre, setSelectedGenre] = useState("Fantasy");

  const handleGenreClick = (genre) => {
    setSelectedGenre(genre);
    onGenreSelect(genre);
  };

  return (
    <div>
      <form action="/" method="GET" className={styles.genreForm}>
        <div className={styles.genreButtons}>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Fantasy" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Fantasy")}
          >
            Fantasy
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Mystery" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Mystery")}
          >
            Mystery
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Fiction" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Fiction")}
          >
            Fiction
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Crime" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Crime")}
          >
            Crime
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Science" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Science")}
          >
            Science
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Narrative" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Narrative")}
          >
            Narrative
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "History" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("History")}
          >
            History
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Sci-Fi" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Sci-Fi")}
          >
            Sci-Fi
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Memoir" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Memoir")}
          >
            Memoir
          </button>
          <button
            className={`${styles.genreButton} ${
              selectedGenre === "Self-Help" && styles.selected
            }`}
            type="button"
            onClick={() => handleGenreClick("Self-Help")}
          >
            Self-Help
          </button>
        </div>
      </form>
    </div>
  );
};

export default GenreForm;
