import React from "react";
import Header from "./components/Reusables/Header";
import ExploreBooks from "./components/Pages/ExploreBooks";
import LandingRoute from "./components/Pages/LandingRoute";
import Footer from "./components/Reusables/Footer";
import { AuthProvider } from "./components/store/AuthContext";
import "./index.css";

function App() {
  return (
    <AuthProvider>
      {" "}
      {/* Wrap your entire application with AuthProvider */}
      <div>
        <Header />
        <MainContent />
        <Footer />
      </div>
    </AuthProvider>
  );
}

const MainContent = () => {
  const { isLoggedIn } = useAuth(); // Assuming useAuth hook is correctly implemented
  return isLoggedIn ? <ExploreBooks /> : <LandingRoute />;
};

export default App;
